@js:
var doc = org.jsoup.Jsoup.parse(result);
var list = doc.select('.links-of-books>li>a').toArray().map(a=>({href:String(a.attr('href')).slice(1).split('/'),text:a.attr('title')}));
var list1 = [], list2 = [];
for (let i in list) {
  if (list[i].href[1]=="1") {
    list1.push(list[i]);
  }
  if (list[i].href[1]=="2") {
    list2.push(list[i]);
  }
}
function Sort(A) {
  let B = [];
  if ((A.length>1) && (parseInt(A[0].href[2]) > parseInt(A[1].href[2]))) {
    for (let i=A.length; i>0; i--) {
      B.push({href:"/"+A[i-1].href.join("/"), text:A[i-1].text});
    }
  } else {
    for (let i=0; i<A.length; i++) {
      B.push({href:"/"+A[i].href.join("/"), text:A[i].text});
    }
  }
  return B;
}
Sort(list2).concat(Sort(list1))

